import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatCash } from "../func/functions";

const timeFrames = ["1D", "1W", "1M", "3M", "YTD", "1Y", "ALL"];

// Initial data structure for time frames
let groupedData = {
  daily: [],
  weekly: [],
  monthly: [],
  threeMonths: [],
  oneYear: [],
  all: [],
};

const InvestmentChart = () => {
  const [selectedTimeFrame, setSelectedTimeFrame] = useState("ALL");
  const [chartData, setChartData] = useState([]);
  const [investmentSummary, setInvestmentSummary] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const dailyPnls = await fetchDailyPnls();
        groupedData = organizeData(dailyPnls); // Organize data into groups initially
        updateChart(selectedTimeFrame);
      } catch (error) {
        console.error("Error fetching PnL data:", error);
      }
    };

    fetchData();
  }, []);

  const organizeData = (dailyPnls) => {
    // Sort by date for accurate aggregation
    dailyPnls.sort((a, b) => new Date(a.entry_date) - new Date(b.entry_date));

    return {
      daily: dailyPnls.slice(-1),
      weekly: dailyPnls.slice(-7),
      monthly: dailyPnls.slice(-30),
      threeMonths: dailyPnls.slice(-90),
      oneYear: dailyPnls.slice(-365),
      all: dailyPnls,
    };
  };

  const updateChart = (timeFrame) => {
    const data = groupedData[timeFrame.toLowerCase()];
    const formattedData = formatChartData(data);
    setChartData(formattedData.chart);
    setInvestmentSummary(formattedData.summary);
  };

  useEffect(() => {
    updateChart(selectedTimeFrame);
  }, [selectedTimeFrame]);

  const formatChartData = (data) => {
    let cumulativeBalance = 0;
    const chart = data.map((entry) => {
      cumulativeBalance += parseFloat(entry.balance);
      return {
        date: new Date(entry.entry_date).toLocaleDateString(),
        balance: cumulativeBalance,
      };
    });

    const summary = {
      balance: cumulativeBalance,
      percentageChange:
        ((cumulativeBalance - data[0].balance) / data[0].balance) * 100,
    };

    return { chart, summary };
  };

  const determineLineColor = () => {
    return investmentSummary && investmentSummary.percentageChange >= 0
      ? "#4caf50"
      : "#e74c3c";
  };

  return (
    <div className="investment-chart-container">
      <div className="investment-summary">
        <h2>Investing</h2>
        <p>{formatCash(investmentSummary?.balance || 0)}</p>
        <p>
          {investmentSummary
            ? `${investmentSummary.percentageChange.toFixed(2)}% All time`
            : "Loading..."}
        </p>
      </div>

      <div className="time-frame-selector">
        {timeFrames.map((frame) => (
          <button
            key={frame}
            onClick={() => setSelectedTimeFrame(frame)}
            className={selectedTimeFrame === frame ? "active" : ""}
          >
            {frame}
          </button>
        ))}
      </div>

      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={chartData}>
          <XAxis dataKey="date" hide />
          <YAxis hide />
          <Tooltip
            formatter={(value) => formatCash(value)}
            labelFormatter={(label) => `Date: ${label}`}
          />
          <Line
            type="monotone"
            dataKey="balance"
            stroke={determineLineColor()}
            strokeWidth={2}
            dot={false}
            animationDuration={500} // Smooth transition effect
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default InvestmentChart;
