import React, { useState, useEffect } from "react";
import NewTrade from "./NewTrade";
import Modal from "./Modal";
import "./styles/recentTrades.css";
import { showSuccessModal, showUnsuccessModal } from "./Utility"; // Import utility functions

const RecentTrades = ({ formatDate }) => {
  const [recentTrades, setRecentTrades] = useState([]);
  const [selectedTrade, setSelectedTrade] = useState(null);
  const [editedTrade, setEditedTrade] = useState(null);
  const [showNewTradeModal, setShowNewTradeModal] = useState(false);

  useEffect(() => {
    // Fetch recent trades when the component mounts
    fetchRecentTrades();
  }, []);

  const fetchRecentTrades = async () => {
    try {
      const response = await fetch("http://localhost:8000/trades/");
      if (!response.ok) {
        throw new Error("Failed to fetch recent trades");
      }
      const data = await response.json();
      setRecentTrades(data);
    } catch (error) {
      console.error("Error fetching recent trades:", error);
    }
  };

  const handleNewTradeClick = () => {
    setShowNewTradeModal(true);
  };

  const handleRowClick = (trade) => {
    setSelectedTrade(trade);
    setEditedTrade({ ...trade });
  };

  const handleCloseModal = () => {
    setSelectedTrade(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedTrade((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/trades/${editedTrade.trade_id}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedTrade),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update trade data");
      }
      handleCloseModal();
      fetchRecentTrades();
      showSuccessModal();
    } catch (error) {
      console.error("Error updating trade data:", error);
      handleCloseModal();
      showUnsuccessModal(error.message);
    }
  };

  return (
    <div className="trades-container">
      <div className="header-card">
        <p className="recent">Trades</p>
        <i
          className="fa-solid fa-plus"
          id="new-btn"
          onClick={handleNewTradeClick}
        ></i>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Options</th>
            <th>Trade Date</th>
            <th>Settle Date</th>
            <th>Price</th>
            <th>QTY</th>
            <th>Principle</th>
            <th>Exit</th>
            <th> % </th>
            <th>Net Amount</th>
            <th>P/L</th>
          </tr>
        </thead>

        <tbody>
          {recentTrades.slice(-15).map((trade) => (
            <tr key={trade.trade_id} onClick={() => handleRowClick(trade)}>
              <td>{trade.trade_id}</td>
              <td>
                {trade.symbol} {formatDate(trade.expiration_date)}{" "}
                {trade.option_type} ${trade.strike_price}
              </td>
              <td>{formatDate(trade.open_date)}</td>
              <td>{trade.close_date ? formatDate(trade.close_date) : ""}</td>
              <td>${trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>
                ${(trade.entry_price * trade.contracts * 100).toFixed(2)}
              </td>{" "}
              <td>{trade.exit_price ? `$${trade.exit_price}` : ""}</td>
              <td>
                {trade.exit_price && trade.entry_price
                  ? `${(
                      ((trade.exit_price - trade.entry_price) /
                        trade.entry_price) *
                      100
                    ).toFixed(2)}%`
                  : ""}
              </td>{" "}
              <td>${(trade.exit_price * trade.contracts * 100).toFixed(2)}</td>{" "}
              <td>${(trade.profit_loss * 100 * trade.contracts).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {showNewTradeModal && (
        <NewTrade
          setShowModal={setShowNewTradeModal}
          fetchRecentTrades={fetchRecentTrades}
        />
      )}
      {selectedTrade && (
        <Modal isOpen={true} onClose={handleCloseModal}>
          <div className="header-card">
            <h1>{selectedTrade.symbol}</h1>
          </div>
          <form onSubmit={handleFormSubmit}>
            <label htmlFor="option_symbol">Option Symbol:</label>
            <input
              type="text"
              name="option_symbol"
              value={editedTrade.option_symbol}
              onChange={handleInputChange}
            />
            <label htmlFor="option_type">Option Type:</label>
            <input
              type="text"
              name="option_type"
              value={editedTrade.option_type}
              onChange={handleInputChange}
            />
            <label htmlFor="strike_price">Strike Price:</label>
            <input
              type="text"
              name="strike_price"
              value={editedTrade.strike_price}
              onChange={handleInputChange}
            />
            <label htmlFor="entry_price">Entry Price:</label>
            <input
              type="text"
              name="entry_price"
              value={editedTrade.entry_price}
              onChange={handleInputChange}
            />
            <label htmlFor="exit_price">Exit Price:</label>
            <input
              type="text"
              name="exit_price"
              placeholder="Exit trade before entering exit price"
              value={editedTrade.exit_price || ""}
              onChange={handleInputChange}
            />
            <label htmlFor="contracts">Contracts:</label>
            <input
              type="text"
              name="contracts"
              value={editedTrade.contracts}
              onChange={handleInputChange}
            />
            <label htmlFor="close_date">Close Date:</label>
            <input
              type="date"
              name="close_date"
              value={editedTrade.close_date || ""}
              onChange={handleInputChange}
            />
            <input type="submit" value="Submit" />
          </form>
        </Modal>
      )}
    </div>
  );
};

export default RecentTrades;
