import React, { useEffect, useRef, useState } from "react";
import "./styles/accountGraph.css";

const AccountGraph = ({ trades, onTimePeriodChange, periodPL }) => {
  const chartRef = useRef(null); // Reference to the canvas element
  const [timePeriod, setTimePeriod] = useState("1D"); // State to track the selected time period

  useEffect(() => {
    // Load Chart.js from the CDN dynamically
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/chart.js";
    script.async = true;
    script.onload = () => {
      // Initialize Chart.js instance after the script has loaded
      const ctx = chartRef.current.getContext("2d");
      const myChart = new window.Chart(ctx, {
        type: "line",
        data: {
          labels: getLabels(timePeriod), // Get labels based on the selected time period
          datasets: [
            {
              data: getData(trades, timePeriod), // Get data based on the selected time period and trades
              borderColor: "green", // Green color for stock price line
              backgroundColor: "rgba(0, 0, 0, 0)", // Transparent background color
              borderWidth: 2, // Width of the line
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false, // Hide legend
            },
          },
          scales: {
            x: {
              grid: {
                display: false, // Hide grid lines
              },
              ticks: {
                display: false, // Hide ticks
              },
              min: 0, // Start y-axis from 0
              max: 1000, // Set max value of y-axis
              position: "center",
            },
            y: {
              display: false, // Hide y-axis
            },
          },
        },
      });

      return () => {
        myChart.destroy(); // Clean up chart on unmount
      };
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script); // Clean up script tag on unmount
    };
  }, [trades, timePeriod]);

  // Function to get labels based on the selected time period
  const getLabels = (period) => {
    // Implement logic to return labels based on the selected time period
    // For example,
    return [];
  };

  // Function to get data based on the selected time period and trades
  const getData = (trades, period) => {
    // Implement logic to return data based on the selected time period and trades
    // For example, if period is "weeks", return data for weeks
    return [];
  };

  // Function to handle time period change
  const handleTimePeriodClick = (period) => {
    setTimePeriod(period); // Update timePeriod state
    onTimePeriodChange(period); // Call the onTimePeriodChange function from props
  };

  return (
    <div className="account-graph">
      <canvas ref={chartRef} /> {/* Canvas for rendering the chart */}
      <div className="time-period-selector">
        <button
          style="fontsize: 1px"
          className="time-period-button"
          onClick={() => handleTimePeriodClick("Today")}
        >
          1D
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodClick("1W")}
        >
          1W
        </button>
        <button
          className="time-period-button"
          onClick={() => handleTimePeriodClick("1M")}
        >
          1M
        </button>
        {/* Add buttons for other time periods as required */}
      </div>
      {/* <div className="period-pl">
        <p>{`${timePeriod} P/L`}</p>
        <p className="cash">{periodPL.toFixed(2)}</p>
      </div> */}
    </div>
  );
};

export default AccountGraph;

