import React, { useState } from "react";
import "./styles/initialCash.css";

const InitialCash = ({ onInitialCashSubmit }) => {
  const [initialCash, setInitialCash] = useState("");
  const [displayedInitialCash, setDisplayedInitialCash] = useState(null);
  const [loading, setLoading] = useState(false);
  const [submissionError, setSubmissionError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSubmissionError("");

    try {
      // Ensure initial cash is a valid number
      if (!isNaN(parseFloat(initialCash))) {
        // Pass initial cash to parent component
        await onInitialCashSubmit(parseFloat(initialCash));
        // Display initial cash above input field
        setDisplayedInitialCash(parseFloat(initialCash));
      }
    } catch (error) {
      console.error("Error submitting initial cash:", error.message);
      setSubmissionError("Error submitting initial cash. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="initial-cash-container">
      <form className="initial" onSubmit={handleSubmit}>
        <label htmlFor="initial-cash">Initial Cash:</label>
        <input
          id="initial-cash"
          type="number"
          placeholder="Enter initial cash amount"
          value={initialCash}
          onChange={(e) => setInitialCash(e.target.value)}
          required
        />

        <button type="submit" disabled={loading}>
          {loading ? "Saving..." : "Save"}
        </button>

        {submissionError && <p className="error-message">{submissionError}</p>}
      </form>
    </div>
  );
};

export default InitialCash;
