import React, { useState, useEffect } from "react";
import { formatDate } from "./Utility"; // Import utility functions
import "./styles/watchlist.css";
import closeIcon from "./assets/close.png"; // Import close icon image
import settingIcon from "./assets/setting.png"; // Import setting icon image

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editedWatchlist, setEditedWatchlist] = useState(null); // Changed variable name

  useEffect(() => {
    fetch("http://localhost:8000/watchlist")
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const handleDeleteItem = (id) => {
    const updatedWatchlist = watchlist.filter((item) => item.watch_id !== id);
    setWatchlist(updatedWatchlist);
    console.log("Updated watchlist:", updatedWatchlist);
  };

  const handleSettingsClick = (item) => {
    setSelectedItem(item);
    setEditedWatchlist(item); // Set edited watchlist to the selected item
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedItem(null);
    setShowModal(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedWatchlist((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(
        `http://localhost:8000/watchlist/${editedWatchlist.watch_id}`, // Use editedWatchlist.watch_id
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(editedWatchlist),
        }
      );
      if (!response.ok) {
        throw new Error("Failed to update watchlist item");
      }
      handleCloseModal();
      // Fetch updated watchlist data after successful update
      fetch("http://localhost:8000/watchlist")
        .then((response) => response.json())
        .then((data) => setWatchlist(data))
        .catch((error) => console.error("Error fetching watchlist:", error));
    } catch (error) {
      console.error("Error updating watchlist item:", error);
      handleCloseModal();
      // Handle error scenario
    }
  };

  return (
    <div className="watchlist">
      <div className="header-card">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item) => (
            <li className="list-item" key={item.watch_id}>
              <img
                src={closeIcon}
                alt="Close"
                className="close-icon"
                onClick={() => handleDeleteItem(item.watch_id)}
              />
              <img
                src={settingIcon}
                alt="Setting"
                className="setting-icon"
                onClick={() => handleSettingsClick(item)}
              />
              <p>{item.symbol}</p>
              <p>${item.current_price}</p>
              <p>${item.target_price}</p>
              <p>{formatDate(item.expiration_date)}</p>
            </li>
          ))}
        </ul>
      </div>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <div className="card">
              <h1>{editedWatchlist.symbol}</h1>
            </div>
            {editedWatchlist && (
              <div>
                <form className="watch-form" onSubmit={handleFormSubmit}>
                  <label htmlFor="current_price">Current Price:</label>
                  <input
                    type="text"
                    name="current_price"
                    value={editedWatchlist.current_price}
                    onChange={handleInputChange}
                  />
                  <label htmlFor="target_price">Target Price:</label>
                  <input
                    type="text"
                    name="target_price"
                    value={editedWatchlist.target_price}
                    onChange={handleInputChange}
                  />
                  <label htmlFor="expiration_date">Exp. Date:</label>
                  <input
                    type="date"
                    name="expiration_date"
                    value={editedWatchlist.expiration_date}
                    onChange={handleInputChange}
                  />
                  <input type="submit" value="Submit" />
                </form>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Watchlist;
