import React, { useState } from "react";
import { createCash } from "../api/CashApi";

const NewCash = ({ onClose, onNewCash }) => {
  const [formData, setFormData] = useState({
    entry_date: "",
    initial_cash: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newCash = await createCash({
        initial_cash: parseFloat(formData.initial_cash),
        entry_date: formData.entry_date,
      });
      onNewCash(newCash); // Notify parent component
      setFormData({
        entry_date: "",
        initial_cash: 0,
      });
      onClose(); // Close form
    } catch (error) {
      console.error("Failed to create cash entry:", error);
    }
  };

  return (
    <div className="new-cash-form">
      <h2>Add New Cash Entry</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="entry_date">Entry Date:</label>
          <input
            type="date"
            id="entry_date"
            name="entry_date"
            value={formData.entry_date}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="initial_cash">Initial Cash Amount:</label>
          <input
            type="number"
            id="initial_cash"
            name="initial_cash"
            value={formData.initial_cash}
            onChange={handleChange}
            required
            step="0.01"
          />
        </div>
        <button type="submit">Add Cash</button>
      </form>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
};

export default NewCash;
