import React, { useState, useEffect } from "react";
import "./styles/watchlist.css"; // Import CSS file
import { formatDate } from "./Utility"; // Import utility functions
import closeIcon from "../assets/close.png"; // Import cancel icon image

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist") // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const handleItemClick = (index) => {
    const updatedWatchlist = [...watchlist];
    updatedWatchlist[index].clicked = !updatedWatchlist[index].clicked;
    setWatchlist(updatedWatchlist);
  };

  return (
    <div className="watchlist">
      <div className="header-card">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item, index) => (
            <li
              key={index}
              className={
                item.target_price < item.current_price
                  ? "watch-item red"
                  : "watch-item green"
              }
              onClick={() => handleItemClick(index)}
            >
              <div className="watch-content">
                <p className="symbol">{item.symbol}</p>
                <p>${item.current_price}</p>
                <p>${item.target_price}</p>
                <p>{formatDate(item.expiration_date)}</p>
                {item.clicked && (
                  <img src={closeIcon} alt="Cancel" className="cancel-icon" />
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Watchlist;
