import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { formatCash, formatDate } from "../func/functions";

const ProgressChart = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [hoveredData, setHoveredData] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        if (data && data.length > 0) {
          const sortedData = data.sort(
            (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
          );
          setChartData(formatChartData(sortedData));
        } else {
          setError("No data available");
        }
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchDailyPNLData();
  }, []);

  const formatChartData = (data) => {
    return data.map((entry) => ({
      date: formatDate(entry.entry_date),
      pnl: parseFloat(entry.close_cash || 0) - parseFloat(entry.open_cash || 0),
    }));
  };

  return (
    <div className="progress-chart-container">
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div className="progress-bar-container">
          {chartData.map((day, index) => (
            <div
              key={index}
              className="progress-bar-row"
              onMouseEnter={() => setHoveredData(day)}
              onMouseLeave={() => setHoveredData(null)}
            >
              <span className="date-label">{day.date}</span>
              <div
                className="progress-bar-day"
                style={{
                  backgroundColor: day.pnl >= 0 ? "#4CAF50" : "#F44336",
                  width: `${Math.abs(day.pnl) * 100}%`,
                  marginLeft: day.pnl >= 0 ? "50%" : "50%",
                  transform: day.pnl < 0 ? "translateX(-100%)" : "none",
                }}
              ></div>
              {hoveredData === day && (
                <div className="tooltip-content">
                  <p>{formatCash(day.pnl)}</p>
                  <p className="tooltip-date">{day.date}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProgressChart;
