// Calculate Total ROI
export const calculateTotalROI = (trades) => {
  if (trades.length === 0) return 0;

  const initialInvestment = trades[0]?.initial_investment || 0;
  const totalReturn = trades.reduce(
    (acc, trade) => acc + (trade.exit_price - trade.entry_price),
    0
  );

  if (initialInvestment === 0) return 0; // Avoid division by zero

  const totalROI = (totalReturn / initialInvestment) * 100; // Convert to percentage
  return totalROI;
};

// Calculate Sharpe Ratio
export const calculateSharpeRatio = (dailyPnls) => {
  if (dailyPnls.length === 0) return 0;

  const averageReturn =
    dailyPnls.reduce((acc, pnl) => acc + pnl, 0) / dailyPnls.length;
  const stdDeviation = Math.sqrt(
    dailyPnls.reduce((acc, pnl) => acc + Math.pow(pnl - averageReturn, 2), 0) /
      dailyPnls.length
  );

  if (stdDeviation === 0) return 0; // Avoid division by zero

  const sharpeRatio = averageReturn / stdDeviation;
  return isNaN(sharpeRatio) ? 0 : sharpeRatio; // Return as a number
};

// Calculate Number of Trades
export const calculateNumberOfTrades = (trades) => {
  return trades.length;
};

// Calculate Winning Trades
export const calculateWinningTrades = (trades) => {
  return trades.filter((trade) => trade.exit_price - trade.entry_price > 0)
    .length;
};

// Calculate Losing Trades
export const calculateLosingTrades = (trades) => {
  return trades.filter((trade) => trade.exit_price - trade.entry_price < 0)
    .length;
};

// Calculate Win Rate
export const calculateWinRate = (trades) => {
  const winningTrades = calculateWinningTrades(trades);
  const totalTrades = calculateNumberOfTrades(trades);
  return totalTrades === 0 ? 0 : (winningTrades / totalTrades) * 100; // Return as a percentage
};

// Calculate Average Win
export const calculateAverageWin = (trades) => {
  const winningTrades = trades.filter(
    (trade) => trade.exit_price - trade.entry_price > 0
  );
  const totalWin = winningTrades.reduce(
    (acc, trade) => acc + (trade.exit_price - trade.entry_price),
    0
  );
  return winningTrades.length === 0 ? 0 : totalWin / winningTrades.length; // Return as a number
};

// Calculate Average Loss
export const calculateAverageLoss = (trades) => {
  const losingTrades = trades.filter(
    (trade) => trade.exit_price - trade.entry_price < 0
  );
  const totalLoss = losingTrades.reduce(
    (acc, trade) => acc + (trade.exit_price - trade.entry_price),
    0
  );
  return losingTrades.length === 0 ? 0 : totalLoss / losingTrades.length; // Return as a number
};

// Calculate Risk-Reward Ratio
export const calculateRiskRewardRatio = (trades) => {
  const averageWin = calculateAverageWin(trades);
  const averageLoss = Math.abs(calculateAverageLoss(trades));
  return averageLoss === 0 ? 0 : averageWin / averageLoss; // Return as a number
};

// Calculate Maximum Drawdown
export const calculateMaximumDrawdown = (dailyPnls) => {
  if (dailyPnls.length === 0) return 0;

  let maxDrawdown = 0;
  let peak = dailyPnls[0];

  dailyPnls.forEach((pnl) => {
    if (pnl > peak) {
      peak = pnl;
    }
    const drawdown = (peak - pnl) / peak;
    if (drawdown > maxDrawdown) {
      maxDrawdown = drawdown;
    }
  });

  return maxDrawdown * 100; // Return as a percentage
};

// Calculate Cash Balance
export const calculateCashBalance = (initialCash, netPL) => {
  const initialCashNumber = Number(initialCash);
  const netPLNumber = Number(netPL);

  if (isNaN(initialCashNumber) || isNaN(netPLNumber)) {
    console.error("Invalid input to calculateCashBalance:", {
      initialCash,
      netPL,
    });
    return 0; // Default to 0 if inputs are invalid
  }

  return initialCashNumber + netPLNumber; // Return as a number
};
