import React, { useState, useEffect } from "react";
import { fetchWatchlists } from "../api/WatchlistApi";
import NewWatchlist from "./NewWatchlist";

const Watchlist = () => {
  const [watchlists, setWatchlists] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Fetch watchlists on component mount
  useEffect(() => {
    const getWatchlists = async () => {
      try {
        const data = await fetchWatchlists();
        setWatchlists(data);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    getWatchlists();
  }, []);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  if (loading) {
    return <div>Loading watchlists...</div>;
  }

  if (error) {
    return <div>Error fetching watchlists: {error}</div>;
  }

  return (
    <div className="watchlist-container">
      <div className="header-card">
        <p className="title">Watchlists</p>
        <i
          className="btn btn-primary fa-solid fa-plus"
          id="watchlist-new-btn"
          onClick={handleOpenModal}
        ></i>
      </div>
      <hr />
      <div className="watch-container">
        {watchlists.map((watchlist) => (
          <div className="watch-item" key={watchlist.id}>
            <div className="watch-item-header">
              <h3>{watchlist.symbol}</h3>
              {/* <p>{watchlist.entry_date}</p> */}
            </div>
            <div className="watch-item-body">
              <div className="watch-item-detail">
                <span className="label">Price:</span>
                <span className="value">${watchlist.price}</span>
              </div>
              <div className="watch-item-detail">
                <span className="label">Target Price:</span>
                <span className="value">${watchlist.target_price}</span>
              </div>
              <div className="watch-item-detail">
                <span className="label">Target Hit:</span>
                <span className="value">
                  {watchlist.target_hit ? "Yes" : "No"}
                </span>
              </div>
              <div className="watch-item-detail">
                <span className="label">Exp. Date:</span>
                <span className="value">{watchlist.exp_date}</span>
              </div>
              <div className="watch-item-detail">
                <span className="label">Plan:</span>
                <span className="value">{watchlist.plan}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {showModal && <NewWatchlist onClose={handleCloseModal} />}
    </div>
  );
};

export default Watchlist;
