// Home.js

import React, { useState, useEffect } from "react";
import "./styles/home.css";
import Account from "./Account";
import NewTrade from "./NewTrade";
import RecentTrades from "./RecentTrades";
import BiggestWinsLosses from "./BiggestWinsLosses";

const Home = () => {
  const [recentTrades, setRecentTrades] = useState([]);

  useEffect(() => {
    fetchRecentTrades(); //get trades from backend
  }, []);

  const fetchRecentTrades = () => {
    fetch("http://localhost:8000/trades/")
      .then((response) => response.json())
      .then((data) => setRecentTrades(data))
      .catch((error) => console.error("Error fetching recent trades:", error));
  };

  // format date string to MM/DD/YYYY format
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const month = (date.getMonth() + 1).toString().padStart(2, "0");
    const day = date.getDate().toString().padStart(2, "0");
    const year = date.getFullYear();
    return `${month}/${day}/${year}`;
  };

  // submit new trade
  const handleNewTradeSubmit = (formData) => {
    // POST request to backend to add new trade
    fetch("http://localhost:8000/trades/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then(() => {
        // const alertMessage = `New ${formData.option_type} option trade for ${formData.option_symbol} has been successfully submitted!`;
        // alert(alertMessage);
        // //replace this with a little notification pop-up that fades after 5 seconds
        handleRefresh();
      })
      .catch((error) => console.error("Error adding new trade:", error));
  };

  // Function to refresh the list of recent trades
  const handleRefresh = () => {
    fetchRecentTrades();
    alert("Successfully updated the list."); // Show alert when list is repopulated
  };

  return (
    <div className="grid-container">
      <Account />
      <NewTrade onTradeSubmit={handleNewTradeSubmit} />
      <RecentTrades
        recentTrades={recentTrades}
        formatDate={formatDate}
        onRefresh={handleRefresh}
      />
      <BiggestWinsLosses />
      {/* <BiggestWinsLosses topProfitableTrades={topProfitableTrades} /> */}
    </div>
  );
};

export default Home;
