// Cash.js

import React, { useState, useEffect } from "react";

const Cash = () => {
  // State to store the fetched cash information
  const [cash, setCash] = useState(null);
  // State to indicate if data is loading
  const [loading, setLoading] = useState(true);
  // State to store any error that occurs during fetching
  const [error, setError] = useState(null);

  // Function to fetch cash information from the backend
  const fetchCash = async () => {
    try {
      // Fetch cash data from your backend API
      const response = await fetch("http://localhost:8000/cash");
      if (!response.ok) {
        throw new Error("Failed to fetch cash data");
      }
      const data = await response.json();
      // Set the fetched cash data in state
      setCash(data);
      // Set loading to false after fetching data
      setLoading(false);
    } catch (error) {
      // Set the error state if fetching fails
      setError(error.message);
      // Set loading to false in case of error
      setLoading(false);
    }
  };

  // UseEffect hook to fetch cash data when component mounts
  useEffect(() => {
    fetchCash();
  }, []);

  // Render loading state if data is still loading
  if (loading) {
    return <p>Loading cash information...</p>;
  }

  // Render error state if an error occurred during fetching
  if (error) {
    return <p>Error fetching cash data: {error}</p>;
  }

  // Render the fetched cash data
  return (
    <div>
      <h2>Cash Information</h2>
      {cash.cash_entries.map((entry, index) => (
        <div key={index}>
          <p>Cash balance: ${entry[0]}</p>
          <p>Last updated: {entry[1]}</p>
        </div>
      ))}
    </div>
  );
};

export default Cash;
