// src/components/controllers/api/AnalysisAPI.js

const API_BASE_URL = "http://localhost:8000";

export const analysisAPI = async (userId, startDate, endDate) => {
  try {
    const response = await fetch(
      `${API_BASE_URL}/analysis/${userId}?start_date=${startDate}&end_date=${endDate}`,
      {
        method: "GET",
        headers: {
          Accept: "application/json",
        },
      }
    );

    if (!response.ok) throw new Error("Error fetching analysis");
    return await response.json();
  } catch (error) {
    console.error("Fetch Analysis Error:", error);
    throw error;
  }
};
