import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { getCash } from "../api/CashApi";
import { Line, XAxis, YAxis, ResponsiveContainer, AreaChart } from "recharts";
import { formatCash } from "../func/functions";

const EQTCurve = () => {
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAndFormatData = async () => {
      try {
        const cashData = await getCash();
        const trades = await fetchTrades();

        if (cashData && trades && trades.length > 0) {
          const startingEquity = parseFloat(cashData.initial_cash || 0);
          const formattedData = formatChartData(trades, startingEquity);
          setChartData(formattedData);
          console.log("Formatted Chart Data:", formattedData);
        } else {
          setError("No trade or cash data available");
        }
      } catch (error) {
        setError("Error fetching trade or cash data");
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAndFormatData();
  }, []);

  const formatChartData = (trades, startingEquity) => {
    let cumulativeEquity = startingEquity;

    const sortedTrades = trades.sort(
      (a, b) => new Date(a.close_date) - new Date(b.close_date)
    );

    return sortedTrades.map((trade, index) => {
      cumulativeEquity += parseFloat(trade.profit_loss || 0) * 100;

      return {
        date: trade.close_date,
        equity: cumulativeEquity,
      };
    });
  };

  return (
    <div className="equity-curve-chart-container">
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={400}>
          <AreaChart data={chartData}>
            <XAxis dataKey="date" tick={true} axisLine={true} />
            <YAxis tickFormatter={formatCash} domain={["dataMin", "dataMax"]} />

            {/* Equity Curve Line */}
            <Line
              type="monotone"
              dataKey="equity"
              stroke="#8A2BE2" // BlueViolet for visibility
              strokeWidth={2}
              dot={{ r: 3 }}
              name="Equity Curve"
            />
          </AreaChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default EQTCurve;
