import React, { useState } from "react";

const NewWatch = ({ onClose, onAdd }) => {
  const [newItem, setNewItem] = useState({
    // Initial state for new watchlist item
    symbol: "",
    current_price: "",
    target_price: "",
    expiration_date: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd(newItem); // Pass the new item data to the parent component
    onClose(); // Close the modal after adding the item
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <div>
          <h2>Add New Watchlist Item</h2>
          <form onSubmit={handleSubmit}>
            <label htmlFor="symbol">Symbol:</label>
            <input
              type="text"
              name="symbol"
              value={newItem.symbol}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="current_price">Current Price:</label>
            <input
              type="text"
              name="current_price"
              value={newItem.current_price}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="target_price">Target Price:</label>
            <input
              type="text"
              name="target_price"
              value={newItem.target_price}
              onChange={handleInputChange}
              required
            />
            <label htmlFor="expiration_date">Exp. date:</label>
            <input
              type="date"
              name="expiration_date"
              value={newItem.expiration_date}
              onChange={handleInputChange}
              required
            />
            <button type="submit">Add Item</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewWatch;
