import React, { useEffect, useState } from "react";
import {
  FaChartLine,
  FaPercentage,
  FaMoneyBillWave,
  FaArrowLeft,
} from "react-icons/fa";
import { fetchDailyPnls } from "../api/DailyPNLApi";

const DailyPNL = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("Error fetching Daily PNL data");
        console.error("Error fetching Daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  // Determine the most recent entry for previous day's closing cash
  const latestData = dailyPNLData[0] || {}; // Assuming data is sorted by date, latest data is at index 0
  const previousClosingCash =
    dailyPNLData.length > 1 ? dailyPNLData[1].close_cash : "N/A";

  return (
    <div className="daily-pnl-container">
      <div className="daily-pnl-card-container">
        {/* Card for Previous Closing Cash */}
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaArrowLeft className="card-icon" />
            <span className="label">Previous Closing Cash:</span>
            <span className="value">${previousClosingCash}</span>
          </div>
        </div>

        {/* Card for Latest Daily PNL */}
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaChartLine className="card-icon" />
            <span className="label">Net P/L:</span>
            <span className="value">${latestData.net_pnl || "N/A"}</span>
          </div>
        </div>
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaMoneyBillWave className="card-icon" />
            <span className="label">Balance:</span>
            <span className="value">${latestData.balance || "N/A"}</span>
          </div>
        </div>
        <div className="daily-pnl-card">
          <div className="card-content">
            <FaPercentage className="card-icon" />
            <span className="label">RoI:</span>
            <span className="value">{latestData.roi || "N/A"}%</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DailyPNL;
