//trading-frontend\src\components\controllers\watchlist\NewWatchlist.js

import React, { useState } from "react";
import { createWatchlist } from "../api/WatchlistApi";

const NewWatchlist = ({ onClose }) => {
  const [formData, setFormData] = useState({
    name: "",
    symbol: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await createWatchlist(formData);
      console.log("Success:", response);
      onClose();
      // Optionally, refresh watchlists or notify the user of success
    } catch (error) {
      console.error("Error:", error);
      alert("Error submitting watchlist");
    }
  };

  return (
    <div className="modal">
      <div className="new-watchlist-container">
        <div className="header-card">
          <p className="title">New Watchlist</p>
          <p className="close-btn" onClick={onClose}>
            &times;
          </p>
        </div>
        <hr />
        <form onSubmit={handleSubmit} className="form-container">
          <label>
            <input
              type="text"
              placeholder="Watchlist Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            <input
              type="text"
              placeholder="Symbol"
              name="symbol"
              value={formData.symbol}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
};

export default NewWatchlist;
