import React, { useEffect, useRef, useState } from "react";
import { fetchTrades } from "./ApiCalls";

const Graph = () => {
  const chartRef = useRef(null);
  const [loading, setLoading] = useState(true);
  const [trades, setTrades] = useState([]);
  const [profits, setProfits] = useState({});

  console.log("Graph component mounted");

  const calculateProfitsForAllTimePeriods = (trades) => {
    console.log("Calculating profits for all time periods");
    const timePeriods = ["1D", "1W", "1M", "3M", "YTD", "1Y", "Max"];
    const profitsData = {};
    const now = new Date();

    const startDates = {
      "1D": new Date(now.setDate(now.getDate() - 1)),
      "1W": new Date(now.setDate(now.getDate() - 7)),
      "1M": new Date(now.setMonth(now.getMonth() - 1)),
      "3M": new Date(now.setMonth(now.getMonth() - 3)),
      YTD: new Date(now.getFullYear(), 0, 1),
      "1Y": new Date(now.setFullYear(now.getFullYear() - 1)),
      Max: new Date(0),
    };

    for (const period of timePeriods) {
      console.log(`Processing period: ${period}`);
      const startDate = startDates[period];
      const filteredTrades = trades.filter(
        (trade) => new Date(trade.close_date) >= startDate
      );

      console.log(`Filtered trades: ${JSON.stringify(filteredTrades)}`);

      const totalProfit = filteredTrades.reduce((sum, trade) => {
        const entryPrice = parseFloat(trade.entry_price);
        const exitPrice = parseFloat(trade.exit_price);
        const contracts = trade.contracts;
        if (!isNaN(exitPrice)) {
          return sum + (exitPrice - entryPrice) * contracts * 100;
        }
        return sum;
      }, 0);

      console.log(`Total profit for period: ${period} is ${totalProfit}`);
      profitsData[period] = totalProfit;
    }

    console.log("Profits data calculated");
    return profitsData;
  };

  useEffect(() => {
    console.log("useEffect hook triggered");
    const fetchData = async () => {
      console.log("Fetching data from API");
      try {
        const fetchedTrades = await fetchTrades();
        console.log(`Fetched trades: ${JSON.stringify(fetchedTrades)}`);
        setTrades(fetchedTrades);
        const calculatedProfits =
          calculateProfitsForAllTimePeriods(fetchedTrades);
        console.log(`Calculated profits: ${JSON.stringify(calculatedProfits)}`);
        setProfits(calculatedProfits);
        setLoading(false);
        console.log("Data fetched and processed successfully");
      } catch (error) {
        console.error("Error fetching trades:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    console.log("Second useEffect hook triggered");
    if (!loading && trades.length > 0) {
      console.log("Creating chart");
      const ctx = chartRef.current.getContext("2d");
      const profitValues = Object.values(profits);
      const backgroundColors = profitValues.map((value) =>
        value >= 0 ? "#bed1be" : "rgb(255, 204, 204)"
      );

      console.log(
        "Creating chart with values: ${JSON.stringify(profitValues)} and colors: ${JSON.stringify(backgroundColors)}"
      );

      const myChart = new window.Chart(ctx, {
        type: "bar",
        data: {
          labels: Object.keys(profits),
          datasets: [
            {
              label: "Profit/Loss",
              data: profitValues,
              backgroundColor: backgroundColors,
              barThickness: 40,
            },
          ],
        },
        options: {
          plugins: {
            legend: {
              display: false,
            },
          },
          scales: {
            x: {
              grid: {
                display: false,
              },
            },
            y: {
              display: true,
              title: {
                display: true,
                text: "Profit/Loss",
              },
              grid: {
                color: (context) =>
                  context.tick.value === 0
                    ? "rgba(0, 0, 0, 1)"
                    : "rgba(0, 0, 0, 0)",
                lineWidth: 0.5,
              },
              ticks: {
                callback: function (value, index, values) {
                  return value === 0 ? value : "";
                },
              },
            },
          },
        },
      });

      console.log("Chart created");

      return () => {
        if (myChart) {
          console.log("Destroying chart");
          myChart.destroy();
        }
      };
    }
  }, [loading, trades, profits]);

  return (
    <div className="graph">
      {loading ? <p>Loading...</p> : <canvas ref={chartRef} />}
    </div>
  );
};

export default Graph;
