import React from "react";

const RecentTrades = ({ recentTrades, formatDate, onRefresh }) => {
  // Function to handle refresh button click
  const handleRefresh = () => {
    onRefresh(); // Call the onRefresh function passed from the parent component
    alert("Successfully updated the list");
  };
  const Button = styled.button`
    background-color: ${(props) => theme[props.theme].default};
    color: white;
    padding: 5px 15px;
    border-radius: 5px;
    outline: 0;
    border: 0;
    text-transform: uppercase;
    margin: 10px 0px;
    cursor: pointer;
    box-shadow: 0px 2px 2px lightgray;
    transition: ease background-color 250ms;
    &:hover {
      background-color: ${(props) => theme[props.theme].hover};
    }
    &:disabled {
      cursor: default;
      opacity: 0.7;
    }
  `;

  return (
    <div className="grid-item-card-bottom-left">
      <div className="card">
        <p>Recent Trades</p>
        <button className="time-period-button" onClick={handleRefresh}>
          Refresh
        </button>
        <a href="https://react.school" target="_blank">
          <Button>Link</Button>
        </a>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Date</th>
            <th>Symbol</th>
            <th>Stock Price</th>
            <th>Type</th>
            <th>Strike Price</th>
            <th>Entry Price</th>
            <th>Contracts</th>
            <th>Exit Price</th>
            <th>Expiration Date</th>
          </tr>
        </thead>

        <tbody>
          {recentTrades.map((trade) => (
            <tr key={trade.trade_id}>
              <td>{trade.trade_id}</td>
              <td>{formatDate(trade.trade_date)}</td>
              <td>{trade.option_symbol}</td>
              <td>{trade.underlying_stock_price}</td>
              <td>{trade.option_type}</td>
              <td>{trade.strike_price}</td>
              <td>{trade.entry_price}</td>
              <td>{trade.contracts}</td>
              <td>{trade.exit_price}</td>
              <td>{formatDate(trade.expiration_date)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RecentTrades;
