export const formatDate = (dateString) => {
  const date = new Date(dateString);
  const month = (date.getMonth() + 1).toString().padStart(2, "0");
  const day = date.getDate().toString().padStart(2, "0");
  const year = date.getFullYear();
  return `${month}/${day}/${year}`;
};

export const showSuccessModal = () => {
  // Create a modal
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "lightgreen";
  modal.style.position = "fixed";
  modal.style.top = "20px";
  modal.style.right = "20px";
  modal.style.borderRadius = "2px";
  modal.style.zIndex = "9999";
  modal.style.fontSize = "12px";
  modal.style.boxShadow = "0 5px 30px 0 rgba(0, 255, 0, 0.1)";

  modal.innerHTML = `
      <div class="modal-content-success">
        <p>Success.</p>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove();
  }, 3000);
};

export const showUnsuccessModal = (errorMessage) => {
  const modal = document.createElement("div");
  modal.classList.add("modal-content");
  modal.style.backgroundColor = "rgb(255, 204, 204)";
  modal.style.position = "fixed";
  modal.style.top = "20px";
  modal.style.right = "20px";
  modal.style.borderRadius = "2px";
  modal.style.zIndex = "9999";
  modal.style.fontSize = "12px";
  modal.style.boxShadow = "0 5px 30px 0 rgba(255, 0, 0, 0.1)";

  modal.innerHTML = `
      <div class="modal-content-error">
        <p>Error:</p>
        <p>${errorMessage}.</p>
      </div>
    `;

  // Append the modal to the body
  document.body.appendChild(modal);

  setTimeout(() => {
    modal.remove();
  }, 3000);
};

// Function to make a div draggable
export const makeElementDraggable = (element) => {
  let pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;

  const header = element.querySelector("#watchlist-header");
  if (header) {
    header.onmousedown = dragMouseDown;
  } else {
    element.onmousedown = dragMouseDown;
  }

  function dragMouseDown(e) {
    e = e || window.event;
    e.preventDefault();
    pos3 = e.clientX;
    pos4 = e.clientY;
    document.onmouseup = closeDragElement;
    document.onmousemove = elementDrag;
  }

  function elementDrag(e) {
    e = e || window.event;
    e.preventDefault();
    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
    pos3 = e.clientX;
    pos4 = e.clientY;
    element.style.top = element.offsetTop - pos2 + "px";
    element.style.left = element.offsetLeft - pos1 + "px";
  }

  function closeDragElement() {
    document.onmouseup = null;
    document.onmousemove = null;
  }
};
