import React, { useState, useEffect } from "react";
import "./styles/watchlist.css"; // Import CSS file
import { formatDate } from "./Utility"; // Import utility functions

const Watchlist = () => {
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/watchlist") // Replace with your backend API URL
      .then((response) => response.json())
      .then((data) => setWatchlist(data))
      .catch((error) => console.error("Error fetching watchlist:", error));
  }, []);

  const handleSymbolChange = (event, index) => {
    const updatedWatchlist = [...watchlist];
    updatedWatchlist[index].symbol = event.target.value;
    setWatchlist(updatedWatchlist);
  };

  const handleCurrentPriceChange = (event, index) => {
    const updatedWatchlist = [...watchlist];
    updatedWatchlist[index].current_price = event.target.value;
    setWatchlist(updatedWatchlist);
  };

  const handleTargetPriceChange = (event, index) => {
    const updatedWatchlist = [...watchlist];
    updatedWatchlist[index].target_price = event.target.value;
    setWatchlist(updatedWatchlist);
  };

  const handleExpirationDateChange = (event, index) => {
    const updatedWatchlist = [...watchlist];
    updatedWatchlist[index].expiration_date = event.target.value;
    setWatchlist(updatedWatchlist);
  };

  return (
    <div className="watchlist">
      <div className="header-card">Watchlist</div>
      <div className="list-container">
        <ul className="cards">
          {watchlist.map((item, index) => (
            <li key={item.id}>
              <input
                type="text"
                value={item.symbol}
                onChange={(event) => handleSymbolChange(event, index)}
              />
              <input
                type="text"
                value={item.current_price}
                onChange={(event) => handleCurrentPriceChange(event, index)}
              />
              <input
                type="text"
                value={item.target_price}
                onChange={(event) => handleTargetPriceChange(event, index)}
              />
              <input
                type="text"
                value={formatDate(item.expiration_date)}
                onChange={(event) => handleExpirationDateChange(event, index)}
              />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Watchlist;
