import React, { useState, useEffect } from "react";
import "./styles/account.css";
import AccountGraph from "./AccountGraph";

const Account = () => {
  const [accountBalance, setAccountBalance] = useState(0);
  const [recentTrades, setRecentTrades] = useState([]);

  useEffect(() => {
    // Fetch recent trades when the component mounts
    fetchRecentTrades();
  }, []);

  useEffect(() => {
    // Calculate total profit/loss for each day and update account balance
    if (recentTrades && recentTrades.length > 0) {
      const tradesByDate = recentTrades.reduce((acc, trade) => {
        const date = trade.trade_date.split(" ")[0];
        if (!acc[date]) {
          acc[date] = [];
        }
        acc[date].push(trade);
        return acc;
      }, {});

      Object.entries(tradesByDate).forEach(([date, trades]) => {
        const totalProfitLoss = trades.reduce((acc, trade) => {
          const profitLoss =
            (trade.exit_price - trade.entry_price) * trade.contracts * 100;
          return acc + profitLoss;
        }, 0);
        setAccountBalance((prevBalance) => prevBalance + totalProfitLoss);
      });
    }
  }, [recentTrades]);

  // Function to fetch recent trades from the backend
  const fetchRecentTrades = () => {
    fetch("http://localhost:8000/trades/")
      .then((response) => response.json())
      .then((data) => setRecentTrades(data))
      .catch((error) => console.error("Error fetching recent trades:", error));
  };

  return (
    <div className="account">
      <div className="card">
        <p>Account</p>
        <p className="cash">{`$`}</p>
      </div>
      {/* <AccountGraph /> */}
    </div>
  );
};

export default Account;
