import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { createDailyPNL } from "../api/DailyPNLApi";

const NewDailyPNL = ({ onClose, onNewDailyPNL }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    date: "",
    net_pnl: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newDailyPNL = await createDailyPNL({
        net_pnl: parseFloat(formData.net_pnl),
        date: formData.date,
      });
      onNewDailyPNL(newDailyPNL);
      onClose();
      navigate("/accounts", { replace: true });
      window.location.reload();
    } catch (error) {
      console.error("Error submitting Daily PNL entry:", error);
      alert("Error submitting Daily PNL entry");
    }
  };

  return (
    <div className="modal">
      <div className="new-daily-pnl-container">
        <div className="header-card">
          <p className="title">Add Daily PNL</p>
          <p className="close-btn" onClick={onClose}>
            &times;
          </p>
        </div>
        <hr />
        <form onSubmit={handleSubmit} className="form-container">
          <label>
            <input
              type="text"
              placeholder="Net P/L"
              name="net_pnl"
              value={formData.net_pnl}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <label>
            <input
              type="date"
              placeholder="Date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </label>
          <br />
          <button type="submit">Add</button>
        </form>
      </div>
    </div>
  );
};

export default NewDailyPNL;
