import React, { useState, useEffect } from "react";
import { fetchTrades } from "../api/TradesAPI";
import { getCash } from "../api/CashApi";
import {
  Line,
  XAxis,
  Tooltip,
  ResponsiveContainer,
  LineChart,
  YAxis,
} from "recharts";
import { formatCash, formatDate } from "../func/functions";

const EQTCurve = () => {
  const [chartData, setChartData] = useState([]);
  const [highlightData, setHighlightData] = useState({ above: [], below: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAndFormatData = async () => {
      try {
        const cashData = await getCash();
        const trades = await fetchTrades();

        if (cashData && trades && trades.length > 0) {
          const startingEquity = parseFloat(cashData.initial_cash || 0);
          const formattedData = formatChartData(trades, startingEquity);
          setChartData(formattedData);
          setHighlightData(calculateHighlightSegments(formattedData));
        } else {
          setError("No trade or cash data available");
        }
      } catch (error) {
        setError("Error fetching trade or cash data");
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAndFormatData();
  }, []);

  const formatChartData = (trades, startingEquity) => {
    let cumulativeEquity = startingEquity;
    const idealIncrement =
      (startingEquity * 3 - startingEquity) / trades.length;

    const sortedTrades = trades.sort(
      (a, b) => new Date(a.close_date) - new Date(b.close_date)
    );

    return sortedTrades.map((trade, index) => {
      cumulativeEquity += parseFloat(trade.profit_loss || 0) * 100;
      const idealEquity = startingEquity + idealIncrement * (index + 1);

      return {
        date: formatDate(trade.close_date),
        equity: cumulativeEquity,
        idealEquity: idealEquity,
      };
    });
  };

  const calculateHighlightSegments = (data) => {
    const aboveSegments = [];
    const belowSegments = [];
    let currentSegment = [];
    let isAbove = null;

    data.forEach((point, index) => {
      const { equity, idealEquity } = point;
      const above = equity > idealEquity;

      if (above !== isAbove) {
        // Close the current segment if we're switching from above to below or vice versa
        if (currentSegment.length > 0) {
          (isAbove ? aboveSegments : belowSegments).push([...currentSegment]);
        }
        currentSegment = [];
        isAbove = above;
      }
      currentSegment.push(point);
    });

    // Add the last segment
    if (currentSegment.length > 0) {
      (isAbove ? aboveSegments : belowSegments).push([...currentSegment]);
    }

    return { above: aboveSegments, below: belowSegments };
  };

  return (
    <div className="equity-curve-chart-container">
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData} style={{ background: "transparent" }}>
            <XAxis dataKey="date" tick={false} axisLine={false} />
            <YAxis tickFormatter={formatCash} hide={true} />

            <Tooltip
              cursor={false}
              content={({ payload }) => {
                if (payload && payload.length) {
                  const { date, equity } = payload[0].payload;
                  return (
                    <div className="tooltip-content">
                      <p>{formatCash(equity)}</p>
                      <p className="trade-date">{date}</p>
                    </div>
                  );
                }
                return null;
              }}
            />

            {/* Highlight Segments Above Ideal Equity */}
            {highlightData.above.map((segment, index) => (
              <Line
                key={`above-${index}`}
                type="monotone"
                dataKey="equity"
                data={segment}
                stroke="lightblue"
                strokeWidth={2}
                dot={false}
              />
            ))}

            {/* Highlight Segments Below Ideal Equity */}
            {highlightData.below.map((segment, index) => (
              <Line
                key={`below-${index}`}
                type="monotone"
                dataKey="equity"
                data={segment}
                stroke="lightcoral"
                strokeWidth={2}
                dot={false}
              />
            ))}

            {/* Ideal Equity Line */}
            <Line
              type="monotone"
              dataKey="idealEquity"
              stroke="#9AD3BC"
              strokeWidth={2}
              dot={false}
              name="Ideal Equity"
            />

            {/* Equity Curve Line */}
            <Line
              type="monotone"
              dataKey="equity"
              stroke="#4A90E2"
              strokeWidth={2}
              dot={{ r: 3 }}
              name="Equity Curve"
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default EQTCurve;

