import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import { getCash } from "../api/CashApi";
import { fetchTransactions } from "../api/TransactionsAPI";
import { LineChart, Line, XAxis, Tooltip, ResponsiveContainer } from "recharts";
import { formatCash, formatDate } from "../func/functions";

const InvestmentChart = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [chartData, setChartData] = useState([]);
  const [investmentSummary, setInvestmentSummary] = useState(null);

  useEffect(() => {
    const fetchInvestmentData = async () => {
      try {
        const dailyPnls = await fetchDailyPnls();
        const cashData = await getCash();

        if (dailyPnls && cashData) {
          const sortedData = dailyPnls.sort(
            (a, b) => new Date(a.entry_date) - new Date(b.entry_date)
          );
          setChartData(formatChartData(sortedData, cashData));
          setInvestmentSummary(
            calculateInvestmentSummary(sortedData, cashData)
          );
        } else {
          setError("No daily PNL or cash data available");
        }
      } catch (error) {
        setError("Error fetching data");
        console.error("Error fetching daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchInvestmentData();
  }, []);

  const calculateInvestmentSummary = (data, cashData) => {
    if (!data.length && !cashData)
      return { balance: 0, percentageChange: 0, latestDate: "", pnl: 0 };

    const initialBalance = parseFloat(cashData.initial_cash || 0);
    const finalBalance = parseFloat(data[data.length - 1]?.close_cash || 0);
    const depositAdjustment = parseFloat(cashData.recent_deposit || 0);
    const withdrawalAdjustment = parseFloat(cashData.recent_withdrawal || 0);

    const adjustedBalance =
      finalBalance + depositAdjustment - withdrawalAdjustment;

    const pnl = adjustedBalance - initialBalance;
    const percentageChange =
      ((adjustedBalance - initialBalance) / initialBalance) * 100;
    const latestDate = formatDate(data[data.length - 1]?.entry_date);

    return {
      balance: adjustedBalance,
      percentageChange: isNaN(percentageChange) ? 0 : percentageChange,
      latestDate: latestDate,
      pnl: pnl,
    };
  };

  // const calculateInvestmentSummary = (data, cashData) => {
  //   if (!data.length && !cashData)
  //     return { balance: 0, percentageChange: 0, latestDate: "", pnl: 0 };

  //   const initialBalance = parseFloat(cashData.initial_cash || 0);
  //   const finalBalance = parseFloat(data[data.length - 1]?.close_cash || 0);
  //   const pnl = finalBalance - initialBalance;
  //   const percentageChange =
  //     ((finalBalance - initialBalance) / initialBalance) * 100;
  //   const latestDate = formatDate(data[data.length - 1]?.entry_date);

  //   return {
  //     balance: finalBalance,
  //     percentageChange: isNaN(percentageChange) ? 0 : percentageChange,
  //     latestDate: latestDate,
  //     pnl: pnl,
  //   };
  // };

  const formatChartData = (data, cashData) => {
    const initialEntry = {
      date: formatDate(cashData.entry_date),
      closingBalance: parseFloat(cashData.initial_cash || 0),
    };

    const formattedData = data.map((entry) => ({
      date: formatDate(entry.entry_date),
      closingBalance: parseFloat(entry.close_cash || 0),
    }));

    return [initialEntry, ...formattedData];
  };

  return (
    <div className="investment-chart-container">
      <div className="summary-section">
        <p className="investment-cash">
          {formatCash(investmentSummary?.balance || 0)}
        </p>
        <p className="investment-roi">
          {investmentSummary
            ? `${formatCash(
                investmentSummary.pnl || 0
              )} (${investmentSummary.percentageChange.toFixed(2)}%)`
            : "Loading..."}
        </p>
        <p className="investment-date">
          {investmentSummary?.latestDate || "Loading..."}
        </p>
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={chartData} style={{ background: "transparent" }}>
            <XAxis dataKey="date" tick={false} axisLine={false} />
            <Tooltip
              cursor={false}
              content={({ payload }) => {
                if (payload && payload.length) {
                  const { date, closingBalance } = payload[0].payload;
                  return (
                    <div className="tooltip-content">
                      <p>{formatCash(closingBalance)}</p>
                      <p className="invest-date">{date}</p>
                    </div>
                  );
                }
                return null;
              }}
            />
            <Line
              type="monotone"
              dataKey="closingBalance"
              stroke="#4a90e2"
              strokeWidth={2}
              dot={false}
            />
          </LineChart>
        </ResponsiveContainer>
      )}
    </div>
  );
};

export default InvestmentChart;
