import React, { useState, useEffect } from "react";

const Trade = () => {
  const [trades, setTrades] = useState([]);
  const [error, setError] = useState(null);

  const fetchTrades = async () => {
    try {
      // Make API call to fetch trades data
      const response = await fetch("http://localhost:8000/trades");
      if (!response.ok) {
        throw new Error("Failed to fetch trades data");
      }
      const data = await response.json();
      setTrades(data.trades);
    } catch (error) {
      setError(error.message);
    }
  };

  // UseEffect hook to fetch trades data when component mounts
  useEffect(() => {
    fetchTrades();
  }, []);

  // Render the component JS
  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Options</th>
            <th>Trade Date</th>
            <th>Settle Date</th>
            <th>Price</th>
            <th>QTY</th>
            <th>Principle</th>
            <th>Exit</th>
            <th>%</th>
            <th>Net Amount</th>
            <th>P/L</th>
          </tr>
        </thead>
        <tbody>
          {trades.map((trade) => (
            <tr key={trade[0]}>
              {" "}
              {/* Using the trade ID as the key */}
              <td>{trade[0]}</td> {/* Trade ID */}
              <td>
                {trade[1]} {trade[2]} ${trade[3]} {trade[4].split(4)}
              </td>{" "}
              {/* Symbol, Option Type, Strike Price */}
              <td>{trade[8]}</td> {/* Trade Date */}
              <td>{trade[9]}</td> {/* Settle Date */}
              <td>${trade[5]}</td> {/* Price */}
              <td>{trade[6]}</td> {/* QTY */}
              <td>${(trade[5] * trade[6]).toFixed(2)}</td> {/* Principle */}
              <td>{trade[10] ? `$${trade[10]}` : ""}</td> {/* Exit */}
              <td>
                {trade[10] && trade[5]
                  ? `${(((trade[10] - trade[5]) / trade[5]) * 100).toFixed(2)}%`
                  : ""}
              </td>{" "}
              {/* % */}
              <td>${(trade[10] * trade[6]).toFixed(2)}</td> {/* Net Amount */}
              <td>${(trade[10] - trade[5]).toFixed(2)}</td> {/* P/L */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Trade;
