import React, { useState, useEffect } from "react";
import { fetchDailyPnls } from "../api/DailyPNLApi";
import {
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { formatCash, formatDate } from "../func/functions";

const InvestmentChart = () => {
  const [dailyPNLData, setDailyPNLData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [view, setView] = useState("daily");
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const fetchDailyPNLData = async () => {
      try {
        const data = await fetchDailyPnls();
        setDailyPNLData(data);
      } catch (error) {
        setError("No Daily PNL data");
        console.error("Error fetching daily PNL data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchDailyPNLData();
  }, []);

  useEffect(() => {
    if (!dailyPNLData.length) return;
    setChartData(prepareChartData());
  }, [view, dailyPNLData]);

  const prepareChartData = () => {
    let data = [];

    if (view === "daily") {
      data = getCurrentWeek(dailyPNLData);
    } else if (view === "weekly") {
      data = getCurrentMonth(dailyPNLData).flat();
    } else if (view === "monthly") {
      data = getMonthsData(dailyPNLData).flat();
    }

    return data.map((entry) => ({
      date: formatDate(entry.entry_date),
      balance: parseFloat(entry.balance || 0),
    }));
  };

  const getCurrentWeek = (data) => {
    const weekData = [];
    const today = new Date();
    const startOfWeek = new Date(
      today.setDate(today.getDate() - today.getDay())
    );
    const endOfWeek = new Date(today.setDate(today.getDate() + 6));

    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      if (entryDate >= startOfWeek && entryDate <= endOfWeek) {
        weekData.push(entry);
      }
    });

    return weekData;
  };

  const getCurrentMonth = (data) => {
    const today = new Date();
    return data.filter(
      (entry) => new Date(entry.entry_date).getMonth() === today.getMonth()
    );
  };

  const getMonthsData = (data) => {
    const months = Array.from({ length: 12 }, () => []);
    data.forEach((entry) => {
      const entryDate = new Date(entry.entry_date);
      months[entryDate.getMonth()].push(entry);
    });
    return months.filter((month) => month.length > 0);
  };

  return (
    <div className="investment-chart-container">
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <>
          <div className="header-card">
            <div className="tab-container">
              <button
                className={`tab-button ${view === "daily" ? "active" : ""}`}
                onClick={() => setView("daily")}
              >
                Daily
              </button>
              <button
                className={`tab-button ${view === "weekly" ? "active" : ""}`}
                onClick={() => setView("weekly")}
              >
                Weekly
              </button>
              <button
                className={`tab-button ${view === "monthly" ? "active" : ""}`}
                onClick={() => setView("monthly")}
              >
                Monthly
              </button>
            </div>
          </div>

          <ResponsiveContainer width="100%" height={300}>
            <ScatterChart>
              <XAxis dataKey="date" type="category" />
              <YAxis dataKey="balance" />
              <Tooltip formatter={(value) => formatCash(value)} />
              <Scatter data={chartData} fill="#4a90e2" />
            </ScatterChart>
          </ResponsiveContainer>
        </>
      )}
    </div>
  );
};

export default InvestmentChart;
